import{_ as e,c as t,o,a}from"./app.c49af70b.js";const s="/assets/fc-database-credentials.7a89f336.png",b=JSON.parse('{"title":"Frequently Asked Questions","description":"","frontmatter":{},"headers":[{"level":2,"title":"Q. My site is hosted on Frappe Cloud. How do I connect to my database?","slug":"q-my-site-is-hosted-on-frappe-cloud-how-do-i-connect-to-my-database"},{"level":2,"title":"Q. How do I view charts/dashboard on my Frappe/ERPNext Site?","slug":"q-how-do-i-view-charts-dashboard-on-my-frappe-erpnext-site"},{"level":2,"title":"Q. How do I join data from multiple data sources?","slug":"q-how-do-i-join-data-from-multiple-data-sources"}],"relativePath":"miscellaneous/faq.md","lastUpdated":1690980886000}'),r={name:"miscellaneous/faq.md"},i=a('<h1 id="frequently-asked-questions" tabindex="-1">Frequently Asked Questions <a class="header-anchor" href="#frequently-asked-questions" aria-hidden="true">#</a></h1><h2 id="q-my-site-is-hosted-on-frappe-cloud-how-do-i-connect-to-my-database" tabindex="-1">Q. My site is hosted on Frappe Cloud. How do I connect to my database? <a class="header-anchor" href="#q-my-site-is-hosted-on-frappe-cloud-how-do-i-connect-to-my-database" aria-hidden="true">#</a></h2><p>If your site is hosted on Frappe Cloud and you have <strong>Dashboard</strong> access then follow these instructions to find your database credentials.</p><ol><li><p>Go to <strong>Backup &amp; Restore</strong> tab or go to</p><p><code>https://frappecloud.com/dashboard/sites/&lt;your-site.frappe.cloud&gt;/database</code></p></li><li><p>Click on the <strong>Access Database</strong> button and then click on enable database access.</p></li><li><p>You will be prompted with a dialog box that will show you the database credentials.</p><div class="border rounded flex items-center justify-center bg-gray-50 w-full py-8"><img class="shadow rounded-sm" src="'+s+'" alt="fc-database-credentials" width="50%"></div></li><li><p>Then follow this <a href="/data-source/overview.html">guide</a> to connect to your database.</p></li></ol><h2 id="q-how-do-i-view-charts-dashboard-on-my-frappe-erpnext-site" tabindex="-1">Q. How do I view charts/dashboard on my Frappe/ERPNext Site? <a class="header-anchor" href="#q-how-do-i-view-charts-dashboard-on-my-frappe-erpnext-site" aria-hidden="true">#</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Check <a href="https://github.com/frappe/insights_docs/assets/25369014/8744b000-550a-479c-89d5-6bfe0bf142b7" target="_blank" rel="noreferrer">this video</a> if you want to embed a chart in your Frappe/ERPNext Workspace</p></div><p>You can export your charts/dashboards as an <code>iframe</code> and embed it in anywhere on your site.</p><ol><li><p>Click on the <strong>Share</strong> button in the visualization tab.</p><p><img src="https://github.com/frappe/insights_docs/assets/25369014/96f43995-a174-4a3d-92bb-f6c9210c7342" alt="Share Chart"></p></li><li><p>Click on the <strong>Copy iframe</strong> button</p><p><img src="https://github.com/frappe/insights_docs/assets/25369014/3b713944-7211-4acc-ad03-45c71235bfd4" alt="Copy iframe link"></p></li></ol><h2 id="q-how-do-i-join-data-from-multiple-data-sources" tabindex="-1">Q. How do I join data from multiple data sources? <a class="header-anchor" href="#q-how-do-i-join-data-from-multiple-data-sources" aria-hidden="true">#</a></h2><p>It is possible to join data from multiple data source but it is quite cumbersome. We are working on improving this experience. For now, you can follow the steps below to join data from multiple data sources.</p><ol><li>Create a query for <strong>each</strong> of the data sources that you want to join.</li><li>Make sure that the there is one <strong>common</strong> column in each of the queries that you want to join.</li><li>After creating, click on the <strong>Store Query</strong> under menu to store in into the <strong>Query Store</strong> data source.</li><li>Repeat the above steps for all the queries that you want to join.</li><li>Now, create a new query and use the <strong>Query Store</strong> data source and join each of the queries by selecting them as a table.</li></ol>',11),n=[i];function d(c,l,h,p,u,m){return o(),t("div",null,n)}const g=e(r,[["render",d]]);export{b as __pageData,g as default};
