import{_ as t}from"./chunks/transform-popup.2c4383b3.js";import{_ as a,c as d,o as e,a as r}from"./app.fe041d52.js";const _=JSON.parse('{"title":"Transformations","description":"","frontmatter":{},"headers":[{"level":2,"title":"Pivot","slug":"pivot"},{"level":2,"title":"Unpivot","slug":"unpivot"}],"relativePath":"query/transformations.md","lastUpdated":1700671768000}'),o={name:"query/transformations.md"},s=r('<h1 id="transformations" tabindex="-1">Transformations <a class="header-anchor" href="#transformations" aria-hidden="true">#</a></h1><p>Transformations are a way to modify the data in your query. They are performed after the data is fetched from the database and before the data is displayed in the query result. You can add multiple transformations to your query.</p><p>You can perform the following transformations by adding a transformation to your query in the Visual Query Builder by clicking on the <strong>+</strong> button next to the <strong>Transform</strong> section:</p><p><img src="'+t+'" alt="Transform Popup"></p><h2 id="pivot" tabindex="-1">Pivot <a class="header-anchor" href="#pivot" aria-hidden="true">#</a></h2><p>Pivot is used to transform the data from a row-based format to a column-based format.</p><p>Example:</p><table><thead><tr><th>ID</th><th>category</th><th>sales</th></tr></thead><tbody><tr><td>1</td><td>Consumables</td><td>10</td></tr><tr><td>2</td><td>Consumables</td><td>20</td></tr><tr><td>3</td><td>Hardware</td><td>30</td></tr></tbody></table><p>After pivoting the data on the <code>category</code> column, the data will look like this:</p><table><thead><tr><th>ID</th><th>Consumables</th><th>Hardware</th></tr></thead><tbody><tr><td>1</td><td>10</td><td></td></tr><tr><td>2</td><td>20</td><td></td></tr><tr><td>3</td><td></td><td>30</td></tr></tbody></table><h2 id="unpivot" tabindex="-1">Unpivot <a class="header-anchor" href="#unpivot" aria-hidden="true">#</a></h2><p>Unpivot is used to transform the data from a column-based format to a row-based format.</p><p>Example:</p><table><thead><tr><th>ID</th><th>Consumables</th><th>Hardware</th></tr></thead><tbody><tr><td>1</td><td>10</td><td></td></tr><tr><td>2</td><td>20</td><td></td></tr><tr><td>3</td><td></td><td>30</td></tr></tbody></table><p>After unpivoting the data with new column name <code>category</code> and <code>sales</code>, the data will look like this:</p><table><thead><tr><th>ID</th><th>Category</th><th>Sales</th></tr></thead><tbody><tr><td>1</td><td>Consumables</td><td>10</td></tr><tr><td>2</td><td>Consumables</td><td>20</td></tr><tr><td>3</td><td>Hardware</td><td>30</td></tr></tbody></table>',16),n=[s];function i(h,l,m,p,c,u){return e(),d("div",null,n)}const y=a(o,[["render",i]]);export{_ as __pageData,y as default};
